class Solution(object):
    def findSubstring(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """
        if len(words) == 0:
            return []

        total_map = {}
        for w in words:
            total_map[w] = total_map.get(w, 0) + 1 

        def isSubStr(s, user_word, word_len):
            if not s:
                return True

            word = s[:word_len]
            if word in total_map and user_word.setdefault(word, 0) < total_map[word]:
                user_word[word] += 1
                return isSubStr(s[word_len:], user_word, word_len)
            else:
                return False


        result = []
        word_len = len(words[0])
        substr_len = word_len * len(words)
        for start_pos in xrange(len(s) - substr_len + 1):
            user_word = {}
            if isSubStr(s[start_pos:start_pos+ substr_len], user_word, word_len):
                result.append(start_pos)

        return result

